<body>
<header id="header">
    <div class="main-menu">
        <div class="container-fluid">
            <div class="row">
                <div class="nav-top">
                    <div class="container">
                        <div class="row align-items-center">
                            <div class="col-md-7 nav-top-left">
                                <div class="row-align-items-center">
                                    <div class="logo">
                                        <a href="<?php echo lang_base_url(); ?>"><img src="<?php echo get_logo($general_settings); ?>" alt="logo"></a>
                                    </div>

                                    <div class="top-search-bar">
                                        <?php echo form_open(lang_base_url() . 'search', ['id' => 'form_validate_search', 'class' => 'form_search_main', 'method' => 'get']); ?>
                                        <div class="left">
                                            <div class="dropdown search-select">
                                                <button type="button" class="btn dropdown-toggle" data-toggle="dropdown">
                                                    <?php if (isset($search_type)): ?>
                                                        <?php echo trans("member"); ?>
                                                    <?php else: ?>
                                                        <?php echo trans("product"); ?>
                                                    <?php endif; ?>
                                                </button>
                                                <div class="dropdown-menu">
                                                    <a class="dropdown-item" data-value="product" href="javascript:void(0)"><?php echo trans("product"); ?></a>
                                                    <a class="dropdown-item" data-value="member" href="javascript:void(0)"><?php echo trans("member"); ?></a>
                                                </div>
                                            </div>
                                            <?php if (isset($search_type)): ?>
                                                <input type="hidden" class="search_type_input" name="search_type" value="member">
                                            <?php else: ?>
                                                <input type="hidden" class="search_type_input" name="search_type" value="product">
                                            <?php endif; ?>
                                        </div>
                                        <div class="right">
                                            <input type="text" name="search" maxlength="300" pattern=".*\S+.*" id="input_search" class="form-control input-search" value="<?php echo (!empty($filter_search)) ? $filter_search : ''; ?>" placeholder="<?php echo trans("search_exp"); ?>" required autocomplete="off">
                                            <button class="btn btn-default btn-search"><i class="icon-search"></i></button>
                                            <div id="response_search_results" class="search-results-ajax"></div>
                                        </div>
                                        <?php echo form_close(); ?>
                                    </div>

                                </div>
                            </div>
                            <div class="col-md-5 nav-top-right">
                                <ul class="nav align-items-center">
                                    <?php if (is_marketplace_active()): ?>
                                        <li class="nav-item nav-item-cart li-main-nav-right">
                                            <a href="<?php echo lang_base_url(); ?>cart">
                                                <i class="icon-cart"></i><span><?php echo trans("cart"); ?></span>
                                                <?php $cart_product_count = get_cart_product_count();
                                                if ($cart_product_count > 0): ?>
                                                    <span class="notification"><?php echo $cart_product_count; ?></span>
                                                <?php endif; ?>
                                            </a>
                                        </li>
                                    <?php endif; ?>

                                    <?php if ($this->auth_check): ?>
                                        <li class="nav-item li-main-nav-right">
                                            <a href="<?php echo lang_base_url(); ?>favorites/<?php echo $this->auth_user->slug; ?>">
                                                <i class="icon-heart-o"></i><?php echo trans("favorites"); ?>
                                            </a>
                                        </li>
                                    <?php else: ?>
                                        <li class="nav-item li-main-nav-right">
                                            <a href="<?php echo lang_base_url(); ?>favorites">
                                                <i class="icon-heart-o"></i><?php echo trans("favorites"); ?>
                                            </a>
                                        </li>
                                    <?php endif; ?>


                                    <!--Check auth-->
                                    <?php if (auth_check()): ?>
                                        <li class="dropdown profile-dropdown">
                                            <a class="dropdown-toggle a-profile" data-toggle="dropdown" href="javascript:void(0)"
                                               aria-expanded="false">
                                                <?php if ($unread_message_count > 0): ?>
                                                    <span class="notification"><?php echo $unread_message_count; ?></span>
                                                <?php endif; ?>
                                                <img src="<?php echo get_user_avatar($this->auth_user); ?>" alt="<?php echo get_shop_name($this->auth_user); ?>">
                                                <span class="username"><?php echo character_limiter(get_shop_name($this->auth_user), 15, '..'); ?></span>
                                                <span class="icon-arrow-down"></span>
                                            </a>
                                            <ul class="dropdown-menu">
                                                <?php if ($this->auth_user->role == "admin"): ?>
                                                    <li>
                                                        <a href="<?php echo admin_url(); ?>">
                                                            <i class="icon-dashboard"></i>
                                                            <?php echo trans("admin_panel"); ?>
                                                        </a>
